mapper.select:
SELECT user_id, password, email, profile_picture_url, username, date_of_birth, user_number
FROM users
WHERE user_id = #{user_id}

mapper.update:
UPDATE users
SET profile_picture_url = #{profile_picture_url}
WHERE user_id = #{user_id}

UPDATE users
SET password = #{password}
WHERE user_id = #{user_id}

